const cart = [];

const productContainer = document.getElementById("box");

const generateStars = (rating) => {
  let stars = '';
  for (let i = 0; i < 5; i++) {
    if (i < rating) {
      stars += '<i class="bx bxs-star text-warning"></i>';
    } else {
      stars += '<i class="bx bx-star text-secondary"></i>';
    }
  }
  return stars;
};

const generateColors = (colors) => {
  return colors.map(color => `<i class="bx bxs-circle ${color}"></i>`).join(' ');
};

const generateProductCards = (products) => {
  return products.map(product => `
    <div class="col">
      <div class="card h-100">
        <img src="${product.img}" class="card-img-top" alt="${product.name}">
        <div class="card-body">
          <h5 class="card-title">${product.name}</h5>
          <div class="rating mb-2">
            ${generateStars(product.rating)}
          </div>
          <p class="card-text d-flex justify-content-between align-items-center">
            <span>&dollar;${product.price.toFixed(2)}</span>
            <div class="colors">
              ${generateColors(product.colors)}
            </div>
          </p>
          <a href="${product.link}" onClick="handleClick(event, '${product.name}')" class="btn btn-primary">Buy Now</a>
        </div>
      </div>
    </div>
  `).join('');
};

productContainer.innerHTML = generateProductCards(products);

const handleClick = (event, productName) => {
  event.preventDefault();
  const product = products.find(p => p.name === productName);
  cart.push(product);
  console.log(cart);
  window.localStorage.setItem('cart', JSON.stringify(cart));
};


